    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String plaintext = txt_plaintext.getText();
        String keyStr = txt_key.getText();

        if (!plaintext.isEmpty() && !keyStr.isEmpty()) {
            try {
                // Chuyển key từ String sang int[]
                int[] key = Arrays.stream(keyStr.split(","))
                                  .mapToInt(Integer::parseInt)
                                  .toArray();

                // Khởi tạo TranspositionCipher và mã hóa theo hàng
                TranspositionCipher transpositionCipher = new TranspositionCipher();
                String ciphertext = transpositionCipher.encryptRowWise(plaintext, key);

                // Hiển thị kết quả
                txt_ciphertext.setText(ciphertext);
                JOptionPane.showMessageDialog(this, "Hoán vị (row-wise) encrypted!");

            } catch (NumberFormatException e) {
                JOptionPane.showMessageDialog(this, "Key must be numbers separated by commas!");
            }
        } else {
            JOptionPane.showMessageDialog(this, "Enter plaintext and key!");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        String plaintext = txt_plaintext.getText();
        String key = txt_key.getText();  // key là chuỗi chữ

        if (!plaintext.isEmpty() && !key.isEmpty()) {
            try {
                // Khởi tạo Keyword Caesar
                KeywordCaesar kc = new KeywordCaesar();

                // Mã hóa
                String ciphertext = kc.encrypt(plaintext, key);

                // Hiển thị ciphertext
                txt_ciphertext.setText(ciphertext);
                JOptionPane.showMessageDialog(this, "Keyword Caesar encrypted successfully!");

            } catch (Exception e) {
                JOptionPane.showMessageDialog(this, "Error: " + e.getMessage());
            }
        } else {
            JOptionPane.showMessageDialog(this, "Please enter plaintext and key!");
        }
    }//GEN-LAST:event_jButton2ActionPerformed

