    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        String ciphertext = txt_ciphertext.getText();
        String keyStr = txt_key.getText();

        if (!ciphertext.isEmpty() && !keyStr.isEmpty()) {
            try {
                // Chuyển key từ String sang int[]
                int[] key = Arrays.stream(keyStr.split(","))
                                  .mapToInt(Integer::parseInt)
                                  .toArray();

                // Khởi tạo TranspositionCipher
                TranspositionCipher transpositionCipher = new TranspositionCipher();

                // Giải mã theo hàng (row-wise)
                String plaintext = transpositionCipher.decryptRowWise(ciphertext, key);

                // Hiển thị plaintext
                txt_plaintext.setText(plaintext);
                JOptionPane.showMessageDialog(this, "Hoán vị decrypted successfully!");

            } catch (NumberFormatException e) {
                JOptionPane.showMessageDialog(this, "Key must be numbers separated by commas!");
            }
        } else {
            JOptionPane.showMessageDialog(this, "Enter ciphertext and key!");
        }
    }//GEN-LAST:event_jButton4ActionPerformed

    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        // TODO add your handling code here:
        String ciphertext = txt_ciphertext.getText();
        String key = txt_key.getText();  // key là chuỗi chữ

        if (!ciphertext.isEmpty() && !key.isEmpty()) {
            try {
                // Khởi tạo Keyword Caesar
                KeywordCaesar kc = new KeywordCaesar();

                // Giải mã
                String plaintext = kc.decrypt(ciphertext, key);

                // Hiển thị plaintext
                txt_plaintext.setText(plaintext);
                JOptionPane.showMessageDialog(this, "Keyword Caesar decrypted successfully!");

            } catch (Exception e) {
                JOptionPane.showMessageDialog(this, "Error: " + e.getMessage());
            }
        } else {
            JOptionPane.showMessageDialog(this, "Please enter ciphertext and key!");
        }
    }//GEN-LAST:event_jButton5ActionPerformed

    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
        // TODO add your handling code here:
        String ciphertext = txt_ciphertext.getText();
        String key = txt_key.getText();

        if (!ciphertext.isEmpty() && !key.isEmpty()) {
            try {
                PlayfairCipher cipher = new PlayfairCipher(key);
                String plaintext = cipher.decrypt(ciphertext);

                txt_plaintext.setText(plaintext);
                JOptionPane.showMessageDialog(this, "Playfair decrypted successfully!");

            } catch (Exception e) {
                JOptionPane.showMessageDialog(this, "Error: " + e.getMessage());
            }
        } else {
            JOptionPane.showMessageDialog(this, "Please enter ciphertext and key!");
        }
    }//GEN-LAST:event_jButton6ActionPerformed

